import React, { useState } from 'react';
import styled from "styled-components";
import Result from './Result';
import PickedResult from './PickedResult';
import UnpickableResult from './UnpickableResult';
import Draw from './Draw';
import { supportedScores } from '../config/config';

const Fixture = (props) => {
    const [pick, setPick] = useState();
    const [team, setTeam] = useState();

    function makePick(event) {
        setPick(event.target.innerText);
        setTeam(event.target.id);
        props.addPick(`${props.fixture.id}:${event.target.id}:${event.target.innerText}`);
    }

    function buildPicks(homeAway) {
        let arr = [];
        supportedScores.forEach(s => {
            arr.push(returnPickStyle(s, homeAway));
        })
        return arr;
    }

    function returnPickStyle(score, homeAway) {
        switch(homeAway) {
            case "home":
                if(props.currentRound) {
                    return props.fixture.home === team && pick === score ? <PickedResult score={score} makePick={makePick} team={props.fixture.home} /> : <Result score={score} makePick={makePick} team={props.fixture.home} />
                } else return <UnpickableResult score={score} makePick={makePick} team={props.fixture.home} />

            case "away":
                if(props.currentRound) {
                    return props.fixture.away === team && pick === score ? <PickedResult score={score} makePick={makePick} team={props.fixture.away} /> : <Result score={score} makePick={makePick} team={props.fixture.away} />
                } else return <UnpickableResult score={score} makePick={makePick} team={props.fixture.away} />

            default:
                return <Draw score={"Draw"} makePick={makePick} team="Draw" pickedResult={pick} pickedTeam={team} />

        }

    }

    return(
        <FixtureStyle>
            <PickStyle>
                <TeamStyle>
                {props.fixture.home}
                </TeamStyle>
                {buildPicks("home")}
           </PickStyle>
            <PickStyle>
            <Draw score={"Draw"} makePick={makePick} team="Draw" pickedResult={pick} pickedTeam={team} />
            </PickStyle>
            <PickStyle>
                <TeamStyle>
                {buildPicks("away")}
                </TeamStyle>
            </PickStyle>
        </FixtureStyle>
    )

}

const FixtureStyle = styled.div`
    border-bottom: 1px solid black;
`
const PickStyle = styled.div`
    display: flex;
    justify-content: space-evenly;
    margin: 10px 0px;
`

const TeamStyle = styled.div`
    width: 100px;
    padding: 5px 0;
`

export default Fixture;

