import React from 'react';
import styled from "styled-components";

const BottomDrawer = (props) => {

    const sendPicks = () => {
        props.sendPicks(props.listOfPicks);
    }

    let numberFixtures = props.data.filter(i => i.game_week === props.currentRound);

    function returnButton() {
        return numberFixtures.length === props.picks.length ? <ReadyButtonBottomDrawerStyle onClick={sendPicks()}>Submit</ReadyButtonBottomDrawerStyle> : <NotReadyButtonBottomDrawerStyle>Submit</NotReadyButtonBottomDrawerStyle>
    }

    return (
        <VisibleBottomDrawerStyle>
            {`Selections ${props.picks.length} out of ${numberFixtures.length}`}
            {returnButton()}
        </VisibleBottomDrawerStyle>
    )
}

const VisibleBottomDrawerStyle = styled.div`
    position: sticky;
    height: 15vh;
    background: white;
    border: 1px solid black;
    bottom: 0px;
    font-size: 2em;
    display: flex;
    flex-direction: column;
    justify-content: space-evenly;
    align-items: center;
`

const ReadyButtonBottomDrawerStyle = styled.div`
    display: inline-block;
    background-color: #4299e1;
    border-radius: 50px;
    padding: 8px 16px;
    font-size: 16px;
    font-weight: 700;
    color: white;
    line-height: 26px;
    width: 50%;
`

const NotReadyButtonBottomDrawerStyle = styled.div`
    display: inline-block;
    background-color: grey;
    border-radius: 50px;
    padding: 8px 16px;
    font-size: 16px;
    font-weight: 700;
    color: white;
    line-height: 26px;
    width: 50%;
`

export default BottomDrawer;
