import React from 'react';
import styled from "styled-components";

const BottomDrawer = (props) => {

    let numberFixtures = props.data.filter(i => i.game_week === props.currentRound);

    return (
        <VisibleBottomDrawerStyle>
            {`Selections ${props.picks.length} out of ${numberFixtures.length}`}
            <ButtonBottomDrawerStyle onClick={() => console.log("press")}>Submit</ButtonBottomDrawerStyle>
        </VisibleBottomDrawerStyle>
    )
}

const VisibleBottomDrawerStyle = styled.div`
    position: sticky;
    height: 15vh;
    background: red;
    border: 1px solid black;
    bottom: 0px;
    font-size: 2em;
    display: flex;
    flex-direction: column;
    justify-content: space-evenly;
`

const ButtonBottomDrawerStyle = styled.div`
    display: inline-block;
    outline: 0;
    border: 0;
    cursor: pointer;
    background-color: #4299e1;
    border-radius: 50px;
    padding: 8px 16px;
    font-size: 16px;
    font-weight: 700;
    color: white;
    line-height: 26px;
`

export default BottomDrawer;
