import React from 'react';
import styled from "styled-components";
import BottomDrawerPick from './BottomDrawerPick';

const BottomDrawer = (props) => {

    console.log(props.listOfPicks.length);

    let numberFixtures = props.data.filter(i => i.game_week === props.currentRound);

    return (
        <VisibleBottomDrawerStyle>
            {`Selections ${props.listOfPicks.length} out of ${numberFixtures}`}
        </VisibleBottomDrawerStyle>
    )
}

const VisibleBottomDrawerStyle = styled.div`
    position: sticky;
    height: 25vh;
    background: red;
    border: 1px solid black;
    bottom: 0px;
`

export default BottomDrawer;
